CREATE TABLE IF NOT EXISTS `client_sys_skill` (
  `id` int(11) NOT NULL COMMENT 'id',
  `lvl` int(11) NOT NULL DEFAULT '1' COMMENT '技能等级',
  `lvl_max` int(10) unsigned NOT NULL DEFAULT '60' COMMENT '最高等级',
  `name` varchar(512) NOT NULL COMMENT '技能显示名称',
  `pre_skills` text NOT NULL COMMENT '前置技能id列表[id1,id2...]',
  `desc` text NOT NULL COMMENT '描述',
  `mp` int(10) unsigned NOT NULL COMMENT '消耗的蓝',
  `fury` int(10) NOT NULL DEFAULT '0' COMMENT '消耗的怒值',
  `equip` tinyint(3) NOT NULL DEFAULT '0' COMMENT '是否需要穿戴武器',
  `type` tinyint(4) NOT NULL DEFAULT '1' COMMENT '技能类型:[0:基本技能,1:职业技能,2:公会技能,3:伴侣技能,4:怪物技能,7:宠物技能]',
  `lead_time` int(10) unsigned NOT NULL COMMENT '引导时间',
  `career` tinyint(4) NOT NULL COMMENT '对应职业:[0:不分职业,1:野蛮人,2:猎魂者,3:魔法师,4:德鲁伊] :{:当type为1时有效}]',
  `active` tinyint(4) NOT NULL DEFAULT '1' COMMENT '是否为主动技能:[0:被动技能,1:主动技能]',
  `trigger_type` tinyint(4) NOT NULL COMMENT '技能触发方式,技能为被动技能是有效[1:无法触发,直接加属性,2:无法释放, 直接加buff(宠物给主人),4:释放被动技能,释放别的技能时几率触发]，主动技能时有效3:防御时主动触发',
  `remote` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否为远程技能:[0:近距技能,1:远距技能]',
  `move` tinyint(3) unsigned NOT NULL COMMENT '是否可移动中释放技能:[0:不可移动,1:可以移动]',
  `delay` tinyint(3) unsigned NOT NULL COMMENT '技能效果是否延迟生效',
  `group` tinyint(4) NOT NULL DEFAULT '0' COMMENT ' 目标类型:[0:单体,1:群体]',
  `use_time` int(10) NOT NULL COMMENT '施法时间(吟唱时间)单位毫秒',
  `use_type` tinyint(4) NOT NULL COMMENT '释放方式:[0:目标方式,1:坐标方式,2自身坐标方式]',
  `target_area` text NOT NULL COMMENT '目标区域描述:(对于单体攻击为距离，对于群体攻击为一个结构)',
  `target_object` tinyint(4) NOT NULL DEFAULT '1' COMMENT '目标对象:[1:敌人,2:友,3:自己,4:队,5:无特定目标,6:主人[宠物技能有效]]',
  `target_round_attach` text NOT NULL COMMENT '攻击给目标周围的附加伤害[value, is_percent]',
  `target_max` int(10) NOT NULL DEFAULT '0' COMMENT '目标数量上限0代表没有限制',
  `role_type` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '目标角色类型:[0:不限定,1:怪,2:人,3:宠]',
  `cd` int(11) NOT NULL COMMENT '冷却时间:(单位毫秒)',
  `condition` text NOT NULL COMMENT '升级条件, json,[等级，神力，银币,  经验，道具]',
  `content` text NOT NULL COMMENT '效果',
  `dec_mp_percent` int(6) unsigned NOT NULL COMMENT '对敌伤害技能,魔法值减少比例百分比',
  `state_enhance` text NOT NULL COMMENT '状态增强伤害效果json[stateid, value, is_percent]',
  `state_rate` tinyint(4) unsigned NOT NULL COMMENT '状态附加概率默认100%, 有一定几率附加效果中所列的状态',
  `repeat_times` tinyint(3) NOT NULL COMMENT '效果重复次数[1-10], 默认为0，代表1次,1次也是代表1次',
  `to_hp` tinyint(4) unsigned NOT NULL COMMENT '技能伤害转成释放者的血量百分数',
  `base_hate` int(11) NOT NULL COMMENT '技能产生的基础仇恨值',
  `battle_stren` int(11) unsigned NOT NULL COMMENT '战力值(仅用于显示)',
  `ico_res` text NOT NULL COMMENT '资源文件id'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='前端临时显示用的技能表';

