CREATE TABLE IF NOT EXISTS `sys_buff` (
  `id` int(10) unsigned NOT NULL COMMENT 'id',
  `name` varchar(50) NOT NULL COMMENT '名称',
  `desc` varchar(100) NOT NULL COMMENT 'buff描述',
  `lvl` int(10) unsigned NOT NULL COMMENT '等级',
  `is_debuff` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT '是否为debuff',
  `type` tinyint(4) unsigned NOT NULL COMMENT '类型:[1:属性,2:恢复,3:功能,4:收益]',
  `key` int(11) NOT NULL COMMENT 'key 对于属性 1:hp 2:mp 3:xp 4:hit 5:dodge',
  `value` text NOT NULL COMMENT '值，依据type而改变',
  `is_percent` tinyint(3) unsigned NOT NULL DEFAULT '0' COMMENT 'value是否为百分数:[0:否,1:是]',
  `last_type` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '持续类型:[1:普通持续一定时间,2:在线时间内有效,3:数值消耗后失效]',
  `last_val` int(10) unsigned NOT NULL COMMENT '持续值:{last_type为1,2时为时间(ms),last_type为3时为值}',
  `last_interval` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '时间间隔(ms)',
  `can_remove` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT 'buff是否可以取消',
  `mutex` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '互斥的buffid'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='buff';

